import React from 'react';

const Hero = () => {
    const heroStyle = {
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        paddingTop: '80px',
        position: 'relative',
        overflow: 'hidden'
    };

    const containerStyle = {
        display: 'grid',
        gridTemplateColumns: 'minmax(0, 1.2fr) minmax(0, 0.8fr)',
        gap: '4rem',
        alignItems: 'center'
    };

    const imageWrapperStyle = {
        position: 'relative',
        borderRadius: '24px',
        overflow: 'hidden',
        boxShadow: '0 20px 40px rgba(0,0,0,0.5)',
        border: '1px solid var(--border-color)',
        aspectRatio: '3/4',
        maxWidth: '400px',
        margin: '0 auto'
    };

    const imgStyle = {
        width: '100%',
        height: '100%',
        objectFit: 'cover',
        filter: 'grayscale(100%) contrast(1.1)', // Black and white effect
        transition: 'all 0.5s ease'
    };

    return (
        <section id="about" style={heroStyle}>
            {/* Background Texture */}
            <div style={{
                position: 'absolute',
                top: 0, left: 0, width: '100%', height: '100%',
                background: 'radial-gradient(circle at 80% 20%, rgba(212, 175, 55, 0.05) 0%, transparent 40%)',
                pointerEvents: 'none',
                zIndex: 0
            }}></div>

            <div className="container fade-in-up" style={{ zIndex: 1, width: '100%' }}>
                <div style={containerStyle} className="hero-grid">
                    <div>
                        <p style={{ color: 'var(--accent-color)', fontWeight: '600', marginBottom: '1.5rem', letterSpacing: '2px', textTransform: 'uppercase', fontSize: '0.9rem' }}>
                            Hi, I am
                        </p>
                        <h1 style={{ fontSize: 'clamp(3rem, 6vw, 5.5rem)', fontWeight: '700', marginBottom: '1.5rem', color: 'var(--text-primary)', lineHeight: '1.1' }}>
                            Muhammed <br/>
                            <span className="text-gradient">Tanseer</span>.
                        </h1>
                        <h2 style={{ fontSize: 'clamp(1.5rem, 3vw, 2rem)', color: 'var(--text-secondary)', marginBottom: '2.5rem', fontFamily: 'var(--font-heading)', fontWeight: '400' }}>
                            Flutter Developer
                        </h2>
                        <p style={{ fontSize: '1.125rem', color: 'var(--text-secondary)', marginBottom: '3rem', maxWidth: '540px', lineHeight: '1.8' }}>
                            Expert in building high-performance iOS and Android applications. 
                            Passionately crafting seamless mobile experiences with <span style={{color: 'var(--primary-color)'}}>Flutter</span>, 
                            Clean Architecture, and pixel-perfect design.
                        </p>
                        <div style={{ display: 'flex', gap: '1.5rem' }}>
                            <a href="#projects" style={{
                                display: 'inline-block',
                                backgroundColor: 'var(--bg-color)',
                                color: 'var(--accent-color)',
                                padding: '1rem 2.5rem',
                                borderRadius: '50px',
                                fontWeight: '600',
                                border: '1px solid var(--accent-color)',
                                transition: 'all 0.3s ease'
                            }} className="hover-lift">
                                View Work
                            </a>
                            <a href="mailto:muhammedtanseerpmna@gmail.com" style={{
                                display: 'inline-block',
                                padding: '1rem 2.5rem',
                                borderRadius: '50px',
                                fontWeight: '600',
                                color: 'var(--text-primary)',
                                transition: 'all 0.3s ease'
                            }}>
                                Contact Me
                            </a>
                        </div>
                    </div>


                    {/* Image Section */}
                    <div className="hero-image-container" style={{ position: 'relative' }}>
                        <div style={imageWrapperStyle}>
                            <img src="./assets/profile.jpg" alt="Muhammed Tanseer" style={imgStyle} />
                            <div style={{
                                position: 'absolute',
                                inset: 0,
                                background: 'linear-gradient(to top, rgba(5,5,5,0.8) 0%, transparent 50%)',
                                pointerEvents: 'none'
                            }}></div>
                        </div>

                        {/* Floating Flutter Logo */}
                        <div className="animate-float flutter-glow" style={{
                            position: 'absolute',
                            top: '-30px',
                            right: '-30px',
                            width: '100px',
                            height: '100px',
                            background: 'rgba(5, 5, 5, 0.8)',
                            backdropFilter: 'blur(10px)',
                            borderRadius: '24px',
                            border: '1px solid var(--border-color)',
                            padding: '1rem',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            zIndex: 2,
                            boxShadow: '0 10px 30px rgba(0,0,0,0.5)'
                        }}>
                             <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 166 202" width="60" height="60">
                                <linearGradient id="flutterGrad" x1="0%" y1="0%" x2="100%" y2="100%">
                                    <stop offset="0%" stopColor="#42A5F5" />
                                    <stop offset="100%" stopColor="#0D47A1" />
                                </linearGradient>
                                <path fill="url(#flutterGrad)" d="M98 2L64 36l42 42l34-34L98 2zM68 76L30 114l38 38l26-26L68 76zm-38 38L2 142l52 52l38-38l-62-42z"/>
                                <path fill="none" stroke="var(--accent-color)" strokeWidth="0" d="M98 2L64 36l42 42l34-34L98 2zM68 76L30 114l38 38l26-26L68 76zm-38 38L2 142l52 52l38-38l-62-42z" />
                                {/* Simplified Flutter Shape approximation for clear rendering */}
                                <path fill="#40C4FF" d="M100 0L53 47L100 94h53L100 0z" />
                                <path fill="#40C4FF" d="M53 95L6 142l47 47l47-47L53 95z" />
                                <path fill="#02539A" fillOpacity="0.4" d="M105 101l-50 48l-8 7l49 43l55-52L105 101z" />
                            </svg>
                        </div>
                    </div>
                </div>
            </div>
            
            {/* Simple Responsive Override for Grid */}
            <style>{`
                @media (max-width: 968px) {
                    .hero-grid {
                        grid-template-columns: 1fr !important;
                        text-align: center;
                        gap: 3rem !important;
                    }
                    .hero-grid > div:first-child {
                        order: 2;
                    }
                    .hero-image-container {
                        order: 1;
                        margin-bottom: 1rem;
                    }
                    .text-gradient {
                        display: inline-block;
                    }
                }
            `}</style>
        </section>
    );
};

export default Hero;
